import {ADD_TO_CART, REMOVE_FROM_CART, SUB_QUANTITY, ADD_QUANTITY, EMPTY_CART} from './actionTypes.js';

const initialState = {
  products: [
      {
      "id": 0,
      "name": "TS1",
      "image": "https://www.assos.com/Webstore%20Images/ss-jersey-usa-cycling_None-2-M.jpg?resizeid=3&resizeh=680&resizew=544",
      "desc": "Nice velo T-shirt",
      "price": "$20",
      "quantity": 2,
      "selected": false
    },
    {
      "id": 1,
      "name": "TS2",
      "image": "https://catalog.koolesport.nl/koolesport/images/maillot-cyclisme-bjorka-tourmalet.jpg",
      "desc": "Nice velo T-shirt",
      "price": "$30",
      "quantity": 1,
      "selected": false
    },
    {
      "id": 2,
      "name": "TS3",
      "image": "https://shop.okgoskirent.com/wp-content/uploads/2019/06/stelvio-18-600x600.jpg",
      "desc": "Nice velo T-shirt",
      "price": "$20",
      "quantity": 3,
      "selected": false
    },
    {
      "id": 3,
      "name": "TS14",
      "image": "https://www.santinicycling.com/img/cms/passo-dello-stelvio-maglia-mc.jpg",
      "desc": "Nice velo T-shirt",
      "price": "$20",
      "quantity": 2,
      "selected": false
    }
  ],
  addedItems:[],
  total: 0
};

const CartReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TO_CART:
      return {
        ...state,
        products: state.products.map(product =>
          product.id === action.id ? {...product, selected: true, } : product,
        ),
      };
    case REMOVE_FROM_CART:
      return {
        ...state,
        products: state.products.map(product =>
          product.id === action.id
            ? {...product, selected: false, quantity: 1}
            : product,
        ),
      };
    case ADD_QUANTITY:
      return {
        ...state,
        products: state.products.map(product =>
          product.id === action.id
            ? {...product, quantity: product.quantity + 1}
            : product,
        ),
      };
    case SUB_QUANTITY:
      return {
        ...state,
        products: state.products.map(product =>
          product.id === action.id
            ? {
                ...product,
                quantity: product.quantity !== 1 ? product.quantity - 1 : 1,
              }
            : product,
        ),
      };
    case EMPTY_CART:
      return {
        ...state,
        products: state.products.map(product =>
          product.selected
            ? {...product, selected: false, quantity: 1}
            : product,
        ),
      };
    default:
      return state;
  }
};
export default CartReducer;
