import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'


class Cart extends Component{
    render(){

        let addedProducts = this.props.products.length ?
            (
                this.props.products.map(product=>{
                    return(

                        <li className="collection-product avatar" key={product.id}>
                                    <div className="product-img">
                                        <img src={product.image} alt={product.image} className=""/>
                                    </div>

                                    <div className="product-desc">
                                        <span className="name">{product.name}</span>
                                        <p>{product.desc}</p>
                                        <p><b>Price: {product.price}$</b></p>
                                        <p>
                                            <b>Quantity: {product.quantity}</b>
                                        </p>
                                        <div className="add-remove">
                                            {/*<Link to="/cart"><i className="material-icons">arrow_drop_up</i></Link>*/}
                                            {/*<Link to="/cart"><i className="material-icons">arrow_drop_down</i></Link>*/}
                                        </div>
                                        <button className="waves-effect waves-light btn pink remove">Remove</button>
                                    </div>

                               </li>
                    )
                })
            ):

             (
                <p>Nothing.</p>
             )

       return(

            <div className="container">
                <div className="cart">
                    <h5 className="text-center text-uppercase">You have ordered:</h5>
                    <ul className="collection">
                        {addedProducts}
                    </ul>
                </div>
            </div>
       )
    }
}

const mapStateToProps = (state)=>{
    return{
        products: state.addedProducts
    }
}

export default connect(mapStateToProps)(Cart)
